import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import './MainPage.css';

function formatTimeDifference(pubDate) {
    const publishedDate = new Date(pubDate);
    const now = new Date();

    const publishedYear = publishedDate.getFullYear();
    const publishedMonth = publishedDate.getMonth();
    const publishedDay = publishedDate.getDate();

    const currentYear = now.getFullYear();
    const currentMonth = now.getMonth();
    const currentDay = now.getDate();

    if (
        publishedYear === currentYear &&
        publishedMonth === currentMonth &&
        publishedDay === currentDay
    ) {
        const diffInMs = now - publishedDate;
        const diffInMinutes = Math.floor(diffInMs / 60000);

        if (diffInMinutes < 1) return '방금 전';
        if (diffInMinutes < 60) return `${diffInMinutes}분 전`;

        const diffInHours = Math.floor(diffInMinutes / 60);
        return `${diffInHours}시간 전`;
    } else {
        return `${publishedDate.getMonth() + 1}.${publishedDate.getDate()}`;
    }
}

function decodeHtmlEntities(str) {
    const parser = new DOMParser();
    return parser.parseFromString(str, 'text/html').documentElement.textContent;
}

function removeHtmlTags(str) {
    return str.replace(/<[^>]+>/g, '');
}

function MainPage() {
    const [news, setNews] = useState([]);
    const [page, setPage] = useState(1);
    const displayCount = 36;
    const [searchQuery, setSearchQuery] = useState('');
    const [currentQuery, setCurrentQuery] = useState('기사');
    const [searchHistory, setSearchHistory] = useState([]);

    const fetchNews = (currentPage, query) => {
        const start = (currentPage - 1) * displayCount + 1;
        const searchTerm = query && query.trim() !== '' ? query : '기사';

        axios
            .get(`http://localhost:8080/searchNews?query=${searchTerm}&display=${displayCount}&start=${start}`)
            .then((response) => {
                const processedNews = response.data.items.map((item) => ({
                    ...item,
                    title: removeHtmlTags(decodeHtmlEntities(item.title)),
                    description: removeHtmlTags(decodeHtmlEntities(item.description)),
                    formattedPubDate: formatTimeDifference(item.pubDate),
                }));

                setNews((prevNews) => {
                    const combinedNews = [...prevNews, ...processedNews];
                    const uniqueNews = Array.from(
                        new Map(combinedNews.map((item) => [item.link, item])).values()
                    );
                    return uniqueNews.sort((a, b) => new Date(b.pubDate) - new Date(a.pubDate));
                });
            })
            .catch((error) => {
                console.error('Error fetching news:', error);
            });
    };

    useEffect(() => {
        setCurrentQuery('기사');
        fetchNews(1, '기사');

        const savedHistory = JSON.parse(localStorage.getItem('searchHistory')) || [];
        setSearchHistory(savedHistory.slice(0, 5));
    }, []);

    const handleSearch = () => {
        if (searchQuery.trim() === '') return;

        setNews([]);
        setPage(1);
        setCurrentQuery(searchQuery.trim());

        const updatedHistory = [
            searchQuery.trim(),
            ...searchHistory.filter((item) => item !== searchQuery.trim()),
        ].slice(0, 5);

        localStorage.setItem('searchHistory', JSON.stringify(updatedHistory));
        setSearchHistory(updatedHistory);

        fetchNews(1, searchQuery.trim());
    };

    const handleDeleteHistory = (query) => {
        const updatedHistory = searchHistory.filter((item) => item !== query);
        localStorage.setItem('searchHistory', JSON.stringify(updatedHistory));
        setSearchHistory(updatedHistory);
    };

    const handleLoadMore = () => {
        const nextPage = page + 1;
        setPage(nextPage);
        fetchNews(nextPage, currentQuery);
    };

    return (
        <div className="MainPage">
            <div className="mainpage-header">
                <h1 className="header-title">
                    <a
                        href="/"
                        style={{
                            textDecoration: 'none',
                            color: 'black',
                        }}
                    >
                        News SE
                    </a>
                </h1>

                <div className="search-box">
                    <input
                        type="text"
                        className="search-text"
                        placeholder="뉴스 검색"
                        value={searchQuery}
                        onChange={(e) => setSearchQuery(e.target.value)}
                    />
                    <button type="button" className="search-button" onClick={handleSearch} />
                </div>

                {/* 검색 기록 드롭다운 */}
                {searchHistory.length > 0 && (
                    <div className="search-history">
                        {searchHistory.map((item, index) => (
                            <div key={index} className="history-item">
                                <span onClick={() => setSearchQuery(item)}>{item}</span>
                                <button onClick={() => handleDeleteHistory(item)}>x</button>
                            </div>
                        ))}
                    </div>
                )}
            </div>

            <div className="mainpage_body">
                {news.map((item, index) => (
                    <div key={index}>
                        <Link
                            to="/extra-page"
                            state={{
                                title: item.title,
                                description: item.description,
                                link: item.link,
                            }}
                            style={{
                                textDecoration: 'none',
                                color: 'black',
                                fontWeight: 'bold',
                            }}
                        >
                            <h2>{item.title}</h2>
                        </Link>
                        <p>{item.formattedPubDate}</p>
                        <a
                            href={item.link}
                            target="_blank"
                            rel="noopener noreferrer"
                            style={{ textDecoration: 'none', color: 'blue' }}
                        >
                            기사 원문 보러가기
                        </a>
                    </div>
                ))}
            </div>

            <div className="mainpage_footer">
                <button onClick={handleLoadMore}>기사 더보기</button>
            </div>
        </div>
    );
}

export default MainPage;
