import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './MainPage.css';

/*************** 시간 표시를 위한 함수 ***************/
// 현재 시간을 기준으로 1분전, 1시간전 이렇게 표시
// 하루가 넘어가면 11.12 이렇게 당일 날짜로 표시

function formatTimeDifference(pubDate) {
    const publishedDate = new Date(pubDate);
    const now = new Date();
    
    // 날짜 부분만 비교하기 위해 각 날짜의 연월일을 추출
    const publishedYear = publishedDate.getFullYear();
    const publishedMonth = publishedDate.getMonth();
    const publishedDay = publishedDate.getDate();

    const currentYear = now.getFullYear();
    const currentMonth = now.getMonth();
    const currentDay = now.getDate();

    // 기사 날짜가 전날인 경우
    if (
        publishedYear === currentYear &&
        publishedMonth === currentMonth &&
        currentDay - publishedDay === 1
    ) {
        return `${publishedDate.getMonth() + 1}.${publishedDate.getDate()}`;
    }

    // 시간 차이를 분 단위로 계산
    const diffInMs = now - publishedDate;
    const diffInMinutes = Math.floor(diffInMs / 60000);

    if (diffInMinutes < 1) return '방금 전';
    if (diffInMinutes < 60) return `${diffInMinutes}분 전`;

    const diffInHours = Math.floor(diffInMinutes / 60);
    if (diffInHours < 24) return `${diffInHours}시간 전`;

    // 그 외의 경우 MM.DD 형식으로 표시
    return `${publishedDate.getMonth() + 1}.${publishedDate.getDate()}`;
}

/********************************************/





function MainPage() {
	
	const [news, setNews] = useState([]);
	const [page, setPage] = useState(1); // 페이지 상태 추가
	const displayCount = 36; // 한 번에 가져올 기사 수
	
	const fetchNews = (currentPage) => {
		// API 요청 시 시작 인덱스 계산
		const start = (currentPage - 1) * displayCount + 1;
	
		axios
			.get(`http://localhost:8080/searchNews?query=검색어&display=${displayCount}&start=${start}`)
			.then(response => {
				// API 응답 확인(items에 어떤 항목들이 있는지 확인)
				console.log('API 응답 데이터:', response.data);

                // 기존 뉴스와 새로 가져온 뉴스를 합친 뒤 날짜순 정렬
                const combinedNews = [...news, ...response.data.items].sort((a, b) => {
                    return new Date(b.pubDate) - new Date(a.pubDate);
                });

                setNews(combinedNews); // 정렬된 데이터를 상태로 설정
            })
            .catch(error => {
                console.error('Error fetching news:', error);
            });
    };
	
	// 첫 페이지의 기사를 가져옴
	useEffect(() => {
		fetchNews(page);
	}, [page]);
	
	// "기사 더 보기" 버튼을 누르면 페이지 증가
	const handleLoadMore = () => {
		setPage(prevPage => prevPage + 1);
	};
	
	
/*************** 메인 페이지 출력 ***************/
// 배치(제목,시간,원문링크)
// "기사 더보기" 버튼

	return (
		<div className="MainPage"> 
			<div className="MainPage_header">
				<h1> 배너 + 검색창 넣기 </h1>
			</div>
			
			<div className="MainPage_body">
				{news.map((item, index) => (
					<div key={index}>				
						<h2>{item.title}</h2>
						<p><strong></strong> {formatTimeDifference(item.pubDate)}</p>
						<a href={item.link} target="blank" rel="noopener noreferrer">기사 원문 보러가기</a>
	                </div>
				))}
			</div>
			
			<div className="MainPage_footer">
				<button onClick={handleLoadMore}>기사 더보기</button>
			</div>
		</div>
	);

// href : 하이퍼링크 연결
// target="blank" : 새 탭에서 화면 열기
// rel="noopener noreferrer" : 보안과 프라이버시 보호를 위해 새 탭에서 열린 페이지가 원래 페이지에 접근하지 못하게 하고, 원래 페이지의 참조 정보도 전달되지 않도록 한다
/********************************************/
	
}

export default MainPage;
