import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './App.css';

function App() {
	const [news, setNews] = useState([]);
	
	useEffect(() => {
		axios.get('http://localhost:8080/searchNews?query=검색어&display=36')
		.then(response => {
			console.log('API 응답 데이터:', response.data); // API 응답 확인(items에 어떤 항목들이 있는지 확인)
			setNews(response.data.items); // 전체 JSON 응답에서 items 배열만 사용하겠다는 의미.
		})
		.catch(error => {
			console.error('Error fetching news:', error);
		});
	}, []);
	




// 메인 페이지
// 배치(제목,시간,원문링크)
	return (
		<div className="App"> 
			{news.map((item, index) => (
				<div key={index}>				
					<h2>{item.title}</h2>
					<p><strong>발행 날짜:</strong> {item.pubDate}</p>
                </div>
			))}
		</div>
	);
	
}





export default App;
