import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import './MainPage.css';

/*************** 시간 표시를 위한 함수 ***************/
// 현재 시간을 기준으로 1분전, 1시간전 이렇게 표시
// 하루가 넘어가면 11.12 이렇게 당일 날짜로 표시

function formatTimeDifference(pubDate) {
    const publishedDate = new Date(pubDate);
    const now = new Date();
    
    // 날짜 비교를 위해 연/월/일을 각각 추출
    const publishedYear = publishedDate.getFullYear();
    const publishedMonth = publishedDate.getMonth();
    const publishedDay = publishedDate.getDate();

    const currentYear = now.getFullYear();
    const currentMonth = now.getMonth();
    const currentDay = now.getDate();

    // 오늘 날짜인지 확인
    if (
        publishedYear === currentYear &&
        publishedMonth === currentMonth &&
        publishedDay === currentDay
    ) {
        // 오늘 날짜면 시간 차이를 계산
        const diffInMs = now - publishedDate;
        const diffInMinutes = Math.floor(diffInMs / 60000);

        if (diffInMinutes < 1) return '방금 전';
        if (diffInMinutes < 60) return `${diffInMinutes}분 전`;

        const diffInHours = Math.floor(diffInMinutes / 60);
        return `${diffInHours}시간 전`;
    } else {
        // 오늘 날짜가 아니면 MM.DD 형식으로 표시
        return `${publishedDate.getMonth() + 1}.${publishedDate.getDate()}`;
    }
}

/********************************************/

/********** HTML 엔터티 디코딩 및 태그 제거 함수 **********/
// 추가된 함수: API 응답 데이터에서 HTML 엔터티를 디코딩
function decodeHtmlEntities(str) {
    const parser = new DOMParser();
    return parser.parseFromString(str, 'text/html').documentElement.textContent;
}

// 추가된 함수: API 응답 데이터에서 HTML 태그를 제거
function removeHtmlTags(str) {
    return str.replace(/<[^>]+>/g, '');
}

/********************************************/

function MainPage() {
	const [news, setNews] = useState([]);
	const [page, setPage] = useState(1); // 페이지 상태 추가
	const displayCount = 36; // 한 번에 가져올 기사 수
	const [searchQuery, setSearchQuery] = useState('');
	const [currentQuery, setCurrentQuery] = useState('기사'); // 현재 검색 중인 쿼리 (기본값: '기사')
	const [searchHistory, setSearchHistory] = useState([]); // 검색 기록 관리
	
	/*************** 검색 기록 로컬 저장 함수 ***************/
	const saveSearchHistory = (query) => {
		let history = JSON.parse(localStorage.getItem('searchHistory')) || [];
		// 중복된 기록은 제거
		history = history.filter(item => item !== query);
		// 새로운 검색어를 맨 앞으로 추가
		history.unshift(query);
		// 최근 5개까지만 유지
		if (history.length > 5) {
			history.pop();
		}
		localStorage.setItem('searchHistory', JSON.stringify(history));
		setSearchHistory(history);
	};
	
	/*************** 로컬 기록 초기화 ***************/
	useEffect(() => {
		const history = JSON.parse(localStorage.getItem('searchHistory')) || [];
		setSearchHistory(history);
	}, []);
	
	const fetchNews = (currentPage, query) => {
		// API 요청 시 시작 인덱스 계산
		const start = (currentPage - 1) * displayCount + 1; // 시작 인덱스 계산
		const searchTerm = query && query.trim() !== '' ? query : '기사'; // 검색어가 없으면 '기사'를 기본값으로 설정
		console.log(`Fetching news for query: ${searchTerm}, page: ${currentPage}, start: ${start}`);
	
		axios
			.get(`http://localhost:8080/searchNews?query=${searchTerm}&display=${displayCount}&start=${start}`)
			.then((response) => {
				// API 응답 확인(items에 어떤 항목들이 있는지 확인)
				//console.log('API 응답 데이터:', response.data);
				
				// 응답 데이터의 pubDate를 처리하여 새로운 배열 생성
				const processedNews = response.data.items.map((item) => ({
                	...item,
                	
                	// 제목에서 HTML 엔터티 디코딩 및 태그 제거
                    title: removeHtmlTags(decodeHtmlEntities(item.title)),
                    // 내용에서 HTML 엔터티 디코딩 및 태그 제거
                    description: removeHtmlTags(decodeHtmlEntities(item.description)),
                    // 날짜를 사용자 친화적인 형식으로 변환
                	formattedPubDate: formatTimeDifference(item.pubDate), // pubDate 포맷 적용
            	}));

                // 기존 데이터와 병합 및 중복 제거
            setNews((prevNews) => {
                const combinedNews = [...prevNews, ...processedNews];
                // 중복 제거 (item.link 또는 다른 고유 식별자로)
                const uniqueNews = Array.from(
                    new Map(combinedNews.map((item) => [item.link, item])).values()
                );
                // 시간순 정렬
                return uniqueNews.sort((a, b) => new Date(b.pubDate) - new Date(a.pubDate));
            });
        })
            .catch(error => {
				
				
                console.error('Error fetching news:', error);
            });
    };
    
	// 초기 화면 및 새로 고침
    useEffect(() => {
		setCurrentQuery('기사');
        fetchNews(1, '기사'); // '기사' 문자열로 전체 기사 요청
    }, []);

    // 검색 버튼 클릭 이벤트
    const handleSearch = () => {
		if (!searchQuery.trim()) return; // 빈 문자열은 무시
        saveSearchHistory(searchQuery.trim()); // 검색 기록 저장
        setNews([]); // 기존 뉴스 초기화
        setPage(1); // 페이지 초기화
        setCurrentQuery(searchQuery.trim() || '기사'); // 검색어가 없으면 기본 검색어 사용
        fetchNews(1, searchQuery.trim() || '기사'); // 검색어로 뉴스 요청
    };
	
	// 검색 기록 클릭 이벤트
	const handleHistoryClick = (query) => {
		setSearchQuery(query); // 검색창에 선택한 기록을 입력
		handleSearch(); // 검색 실행
	};

	// 검색 기록 삭제 이벤트
	const handleDeleteHistory = (query) => {
		const updatedHistory = searchHistory.filter(item => item !== query);
		localStorage.setItem('searchHistory', JSON.stringify(updatedHistory));
		setSearchHistory(updatedHistory);
	};
    
	// "기사 더 보기" 버튼을 누르면 페이지 증가
	const handleLoadMore = () => {
		const nextPage = page + 1; // 다음 페이지
        setPage(nextPage); // 페이지 상태 업데이트
        fetchNews(nextPage, currentQuery); // 현재 검색 중인 쿼리로 데이터 요청
	};
	
	return (
		<div className="MainPage"> 
			<div className="mainpage-header">
				<h1 className="header-title">
					<a href='/'
						style={{
								textDecoration: 'none',
								color: 'black'
							  }}
					>News SE</a>
				</h1>
				
				<div className="search-box">
					<input
						type="text"
						className="search-text"
						placeholder="뉴스 검색"
						value={searchQuery}
						onChange={(e) => setSearchQuery(e.target.value)} // 입력된 값을 상태에 저장
					/>
					<button
						type="button"
						className="search-button"
						onClick={handleSearch} // 검색 버튼 클릭 시 handleSearch 호출
					/>
				</div>
				
				{/* 검색 기록 드롭다운 */}
				{searchHistory.length > 0 && (
					<div className="search-history">
						{searchHistory.map((item, index) => (
							<div className="history-item" key={index}>
								<span onClick={() => handleHistoryClick(item)}>{item}</span>
								<button onClick={() => handleDeleteHistory(item)}>x</button>
							</div>
						))}
					</div>
				)}
				
			</div>
			
			<div className="mainpage_body">
				{news.map((item, index) => (
					<div key={index}>
						<Link
						    to="/extra-page"
						    state={{
						        title: item.title,
						        description: item.description,
						        link: item.link,
						    }}
						    style={{
								textDecoration: 'none',
								color: 'black',
								fontWeight: 'bold',
							}}
						    
						>
						    <h2>{item.title}</h2>
						</Link>
                        <p>{item.formattedPubDate}</p>
                        <a
                        	href={item.link}
                        	target="_blank"
                        	rel="noopener noreferrer"
                        	style={{ textDecoration: 'none', color: 'blue'}}
                        >기사 원문 보러가기</a>
                    </div>
                ))}
			</div>
			
			<div className="mainpage_footer">
