import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import './MainPage.css';

/*************** 시간 표시를 위한 함수 ***************/
// 현재 시간을 기준으로 1분전, 1시간전 이렇게 표시
// 하루가 넘어가면 11.12 이렇게 당일 날짜로 표시

function formatTimeDifference(pubDate) {
    const publishedDate = new Date(pubDate);
    const now = new Date();
    
    // 날짜 비교를 위해 연/월/일을 각각 추출
    const publishedYear = publishedDate.getFullYear();
    const publishedMonth = publishedDate.getMonth();
    const publishedDay = publishedDate.getDate();

    const currentYear = now.getFullYear();
    const currentMonth = now.getMonth();
    const currentDay = now.getDate();

    // 오늘 날짜인지 확인
    if (
        publishedYear === currentYear &&
        publishedMonth === currentMonth &&
        publishedDay === currentDay
    ) {
        // 오늘 날짜면 시간 차이를 계산
        const diffInMs = now - publishedDate;
        const diffInMinutes = Math.floor(diffInMs / 60000);

        if (diffInMinutes < 1) return '방금 전';
        if (diffInMinutes < 60) return `${diffInMinutes}분 전`;

        const diffInHours = Math.floor(diffInMinutes / 60);
        return `${diffInHours}시간 전`;
    } else {
        // 오늘 날짜가 아니면 MM.DD 형식으로 표시
        return `${publishedDate.getMonth() + 1}.${publishedDate.getDate()}`;
    }
}

/********************************************/


function MainPage() {
	
	const [news, setNews] = useState([]);
	const [page, setPage] = useState(1); // 페이지 상태 추가
	const displayCount = 36; // 한 번에 가져올 기사 수
	const [searchQuery, setSearchQuery] = useState('');
	
	const fetchNews = (currentPage, query) => {
		// API 요청 시 시작 인덱스 계산
		const start = (currentPage - 1) * displayCount + 1; // 시작 인덱스 계산
		const searchTerm = query && query.trim() !== '' ? query : '검색어'; // 검색어가 없으면 '검색어'를 기본값으로 설정
	
		axios
			.get(`http://localhost:8080/searchNews?query=${searchTerm}&display=${displayCount}&start=${start}`)
			.then((response) => {
				// API 응답 확인(items에 어떤 항목들이 있는지 확인)
				//console.log('API 응답 데이터:', response.data);
				
				// 응답 데이터의 pubDate를 처리하여 새로운 배열 생성
				const processedNews = response.data.items.map((item) => ({
                	...item,
                	formattedPubDate: formatTimeDifference(item.pubDate), // pubDate 포맷 적용
            	}));

                // 기존 데이터와 병합 및 중복 제거
            setNews((prevNews) => {
                const combinedNews = [...prevNews, ...processedNews];
                // 중복 제거 (item.link 또는 다른 고유 식별자로)
                const uniqueNews = Array.from(
                    new Map(combinedNews.map((item) => [item.link, item])).values()
                );
                // 시간순 정렬
                return uniqueNews.sort((a, b) => new Date(b.pubDate) - new Date(a.pubDate));
            });
        })
            .catch(error => {
				
				
                console.error('Error fetching news:', error);
            });
    };
	
	// 첫 페이지의 기사를 가져옴
	useEffect(() => {
		fetchNews(page, searchQuery); // 검색어를 포함해서 요청
	}, [page, searchQuery]);
	
	// 검색 버튼 클릭 이벤트
	const handleSearch = () => {
		setNews([]); // 기존 뉴스 초기화
		setPage(1); // 페이지 초기화
		const query = searchQuery.trim() === '' ? '검색어' : searchQuery; // 빈 문자열 처리
		fetchNews(1, query); // 검색 수행
	}
	
	// "기사 더 보기" 버튼을 누르면 페이지 증가
	const handleLoadMore = () => {
		setPage((prevPage) => prevPage + 1);
	};
	
	
/*************** 메인 페이지 출력 ***************/
// 배치(제목,시간,원문링크)
// "기사 더보기" 버튼

	return (
		<div className="MainPage"> 
			<div className="mainpage-header">
				<h1 className="header-title">
					<a href='/'
						style={{
								textDecoration: 'none',
								color: 'black'
							  }}
					>News SE</a>
				</h1>
				
				<div className="search-box">
					<input
						type="text"
						className="search-text"
						placeholder="뉴스 검색"
						value={searchQuery}
						onChange={(e) => setSearchQuery(e.target.value)} // 입력된 값을 상태에 저장
					/>
					<button
						type="button"
						className="search-button"
						onClick={handleSearch} // 검색 버튼 클릭 시 handleSearch 호출
					/>
				</div>
				
			</div>
			
			<div className="mainpage_body">
				{news.map((item, index) => (
					<div key={index}>
						<Link
						    to="/extra-page"
						    state={{
						        title: item.title,
						        description: item.description,
						        link: item.link,
						    }}
						    style={{
								textDecoration: 'none',
								color: 'black',
								fontWeight: 'bold',
							}}
						    
						>
						    <h2>{item.title}</h2>
						</Link>
                        <p>{item.formattedPubDate}</p>
                        <a
                        	href={item.link}
                        	target="_blank"
                        	rel="noopener noreferrer"
                        	style={{ textDecoration: 'none', color: 'blue'}}
                        >기사 원문 보러가기</a>
                    </div>
                ))}
			</div>
			
			<div className="mainpage_footer">
				<button onClick={handleLoadMore}>기사 더보기</button>
			</div>
		</div>
	);

// href : 하이퍼링크 연결
// target="blank" : 새 탭에서 화면 열기
// rel="noopener noreferrer" : 보안과 프라이버시 보호를 위해 새 탭에서 열린 페이지가 원래 페이지에 접근하지 못하게 하고, 원래 페이지의 참조 정보도 전달되지 않도록 한다
/********************************************/
	
}

export default MainPage;
