import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './App.css';

function App() {
	const [news, setNews] = useState([]);
	
	useEffect(() => {
		axios.get('http://localhost:8080/searchNews?query=검색어')
		.then(response => {
			console.log('API 응답 데이터:', response.data); // API 응답 확인(items에 어떤 항목들이 있는지 확인)
			setNews(response.data.items); // 전체 JSON 응답에서 items 배열만 사용하겠다는 의미.
		})
		.catch(error => {
			console.error('Error fetching news:', error);
		});
	}, []);
	
	function extractDomain(url) {
    try {
        const urlObj = new URL(url);
        return urlObj.hostname.replace('www.', ''); // 'www.' 제거
    } catch (e) {
        return '출처 없음'; // URL이 유효하지 않을 때 표시
    }
}
	
	return (
		<div className="App"> 
			{news.map((item, index) => (
				<div key={index}>
					<p><strong>신문사:</strong> {extractDomain(item.originalLink)}</p> 
					<p><strong>발행 날짜:</strong> {item.pubDate}</p>
                    <h2>{item.title}</h2>
                    <img src={item.thumbnail || '기본 이미지 URL'} alt="기사 이미지" />
                    <p>{item.description}</p>
                </div>
			))}
		</div>
	);
	
}





export default App;
